from Bio import Phylo
from Bio.Phylo.TreeConstruction import DistanceMatrix, DistanceTreeConstructor


def extract_seq(my_fasta_file):
    my_opened_file = open(my_fasta_file, "r")
    seq = ""
    Organism = ""
    f = {}
    for line in my_opened_file.readlines():
        line1 = line.strip()
        if line1.startswith(">"):
            f.update({Organism: seq})
            seq = ""
            Organism = line1[1:]
        else:
            seq = seq + line1
    f.update({Organism: seq})
    my_opened_file.close()
    return f


def calc_kmer(seq, klen):
    l = []
    for i in range(0, len(seq) - klen + 1):
        l.append(seq[i:i + klen])
    return l


def num_of_occur(l):
    d = {}
    for i in l:
        i = str(i)
        if i not in d.keys():
            d.update({i: 1})
        else:
            d[i] = d[i] + 1
    return d


def creatdic(kmershared, kmerseq1, kmerseq2):
    d = {}
    for i in range(len(kmershared)):
        d.update({kmershared[i]: [kmerseq1.count(kmershared[i]), kmerseq2.count(kmershared[i])]})
    return d


def sumfreq(dic):
    d = {}
    for i in dic.keys():
        d.update({i: sum(dic[i])})
    return d


def diff(dic):
    d1 = {}
    d2 = {}
    for i in dic.keys():
        if dic[i][0] > dic[i][1]:
            d1.update({i: (dic[i][0] - dic[i][1])})
        elif dic[i][1] > dic[i][0]:
            d2.update({i: (dic[i][1] - dic[i][0])})
    return d1, d2


def presentinone(d, su):
    l = []
    for i in d.keys():
        c = d[i] / su[i]
        l.append(c)
    s = sum(l)
    return s


def calcdis(s1, s2, n1, n2):
    distance = ((s1 / n1) + (s2 / n2)) / 2
    return distance


def calc_distance_between_two_seq(seq1="ACGTACATAC", seq2="TACGACG", numk=2):
    kmer_seq1 = calc_kmer(seq1, numk)
    kmer_seq2 = calc_kmer(seq2, numk)
    k = kmer_seq1 + kmer_seq2
    kmer_in_both_sequences = set(k)
    kmer_shared = list(kmer_in_both_sequences)
    occurence = creatdic(kmer_shared, kmer_seq1, kmer_seq2)
    summ = sumfreq(occurence)
    d1, d2 = diff(occurence)
    pre1 = presentinone(d1, summ)
    pre2 = presentinone(d2, summ)
    n1 = len(seq1) - numk + 1
    n2 = len(seq2) - numk + 1
    dist = calcdis(pre1, pre2, n1, n2)
    return dist


def distance_matrix(data, key):
    distancematrix = [[0] * len(key) for i in range(len(key))]  # Create an empty distance matrix
    for i in range(len(key)):  # Calculate the distance between each pair of points
        for j in range(i + 1, len(key)):
            distance = calc_distance_between_two_seq(data[key[i]], data[key[j]], 7)
            distancematrix[i][j] = distance
            distancematrix[j][i] = distance
    return distancematrix


def extract_keys(f):
    li = []
    for i in f.keys():
        if i != "":
            li.append(i)
    return li


def Make_Distance_Matrix_Lower_Triangle(dist):
    new = []
    temp = []
    for i in range(len(dist)):
        for j in range(len(dist[i])):
            if dist[i][j] == 0:
                temp.append(dist[i][j])
                break
            else:
                temp.append(dist[i][j])
        new.append(temp)
        temp = []
    return new


