def UPGMA(distance_matrix, labels):
    # Identify the shortest pairwise distance in the matrix. This identifies the two sequences to be clustered.
    # Join the two sequences identified.
    # The pair should be linked at a depth that is half of the shortest pairwise distance.
    # The tip-to-tip distance between the joined elements will equal the shortest distance.
    newick = ""
    while len(distance_matrix) > 1:
        seq1, seq2, depth, tip_to_tip, index, n = cluster(distance_matrix, labels)
        newick += n
        distance_matrix, labels = update_matrix(distance_matrix, labels, seq1, seq2, index)
    return newick


# The last element of the labels list represents the root of the tree


def cluster(mat, label):
    # Identify the shortest pairwise distance
    min_distance = float("inf")  # float("inf") is a special value that represents positive infinity in Python.
    min_indices = (0, 0)
    for i in range(len(mat)):
        for j in range(i):
            if mat[i][j] < min_distance:
                min_distance = mat[i][j]
                min_indices = (i, j)
    # Join the two sequences identified
    if min_indices[0] < min_indices[1]:
        sequence1 = label[min_indices[0]]
        sequence2 = label[min_indices[1]]
    else:
        sequence1 = label[min_indices[1]]
        sequence2 = label[min_indices[0]]
    depth = float(min_distance) / 2
    shortestdistance = min_distance
    neww = "(" + sequence1 + "," + sequence2 + ")" + ":" + str(shortestdistance)
    return sequence1, sequence2, depth, shortestdistance, min_indices, neww


def update_matrix(mat, label, clade1, clade2, index):
    # Find the indices of clade1 and clade2 in the labels list
    index1 = label.index(clade1)
    index2 = label.index(clade2)
    new_clade = clade1 + clade2
    label.remove(clade1)
    label.remove(clade2)
    row, col = index  # insert col
    if col < row:
        label.insert(col, new_clade)
    else:
        label.insert(row, new_clade)
    # Create a new matrix with one fewer row and column
    new_matrix = [row[:index1] + row[index1 + 1:index2] + row[index2 + 1:] for i, row in enumerate(mat) if
                  i != index1 and i != index2]
    new_row = []
    nnn = []
    for i, row in enumerate(mat):
        if i != index1 and i != index2:
            new_row.append((mat[i][index1] + mat[i][index2]) / 2)

    for i in range(len(new_row)):
        new = new_matrix[i]
        new.insert(col, float(new_row[i]))
        nnn.append(new)
    new_row.insert(col, 0)
    nnn.insert(col, new_row)
    return nnn, label


def getNewick(cluster, depth, labels, newick):
    for i in range(len(cluster)):
        if type(cluster[i]) is list:
            newick += "("
            newick = getNewick(cluster[i], depth + 1, labels, newick)
            newick += ")"
        else:
            newick += labels[cluster[i]] + ":" + str(depth)
    return newick + ","
    

